        <div>
            {
                robots.filter((robot) => robot.name.contains(inputValue))
                      .map((user, i) => {
                    return (
                    <Card 
                        key={i} 
                        id={robots[i].id} 
                        name={robots[i].name} 
                        email={robots[i].email}
                    />
                    )
                })
            }
        </div>
    );
};

