import React from "react";
import { useState } from "react";

function Button(props) {
    return (
        <button onClick={props.onClick}>
            {props.children}
        </button>
    )
};

function InfoList(films) {
    return (
        <ul>
        {films.map((key) => {
            return <li key={key}> {key}: {films[key]}</li>
        })}
        </ul>
    )
};

